import { useState } from "react";
import "./addstock.css"
import axios from "axios"
function AddStock(){
    const [title,settitle]=useState('')
    const [description,setdescription]=useState('')
    const [imgurl,setimgurl]=useState('')
    const [cost,setcost]=useState('')
    const [rate,setrate]=useState('')
    function upload(){
        const newdata={
            title:title,
            description
        }
        axios.post("http://localhost:6060/addstock",newdata)
    }
    return (
        <>
            <form className="addform" onSubmit={upload} >
                <h1>Add New Product</h1>
                <input type='text' placeholder="Title" name="title"  value={title} />
                <input type='text' placeholder="Description" name="description" value={description} />
                <input type='text' placeholder="image URL" name="imgurl" value={imgurl}/>
                <input type='text' placeholder="cost"name="cost" value={cost} />
                <input type='text' mix='5' placeholder="Rating" name="rate" value={rate} />
                <button>Upload</button>
            </form>
        </>
    )
}
export default AddStock;
