import { useState } from "react";
import "./addstock.css"
import axios from "axios"
function AddStock(){
    const [title,settitle]=useState('')
    const [description,setdescription]=useState('')
    const [imgurl,setimgurl]=useState('')
    const [cost,setcost]=useState('')
    const [rate,setrate]=useState('')

    function updateTitle(event){
        settitle(event.target.value)
    }
    function updatedesc(event){
        setdescription(event.target.value)
    }
    function updateurl(event){
        setimgurl(event.target.value)
    }
    function updatecost(event){
        setcost(event.target.value)
    }
    function updaterate(event){
        setrate(event.target.value)
    }
 
    function upload(event){
        event.preventDefault();
        const newdata={
            title:title,
            description:description,
            imgurl:imgurl,
            cost:cost,
            rate:rate
        }
        axios.post("http://localhost:6060/addstock",newdata)
        .then(res=>{
            // console.log(res)
            console.log("data sent")  
            settitle('');
            setdescription('')
            setcost('')
            setimgurl('')
            setrate('')
        })
        .catch(err=>{
            console.log(err)
        })

    }
    return (
        <>
            <form className="addform" onSubmit={upload} autoComplete="off" >
                <h1>Add New Product</h1>
                <input type='text' placeholder="Title"   value={title} onChange={updateTitle} />
                <input type='text' placeholder="Description" value={description} onChange={updatedesc} />
                <input type='text' placeholder="image URL" value={imgurl} onChange={updateurl} />
                <input type='text' placeholder="cost" value={cost} onChange={updatecost} />
                <input type='text'  placeholder="Rating"  value={rate} onChange={updaterate} />
                <button disabled={!navigator.onLine} >Upload</button>
                
            </form>
        </>
    )
}
export default AddStock;
