import { useState } from "react";
import "./addstock.css"
import axios from "axios"

import {Button} from "@mui/material"
import TextField from '@mui/material/TextField';
function AddStock(){
    const [title,settitle]=useState('')
    const [description,setdescription]=useState('')
    const [imgurl,setimgurl]=useState('')
    const [cost,setcost]=useState('')
    const [rate,setrate]=useState('')

    function updateTitle(event){
        settitle(event.target.value)
    }
    function updatedesc(event){
        setdescription(event.target.value)
    }
    function updateurl(event){
        setimgurl(event.target.value)
    }
    function updatecost(event){
        setcost(event.target.value)
    }
    function updaterate(event){
        setrate(event.target.value)
    }
 
    function upload(event){
        event.preventDefault();
        const newdata={
            title:title,
            description:description,
            imgurl:imgurl,
            cost:cost,
            rate:rate
        }
        axios.post("http://localhost:6060/addstock",newdata)
        .then(res=>{
            // console.log(res)
            console.log("data sent")  
            settitle('');
            setdescription('')
            setcost('')
            setimgurl('')
            setrate('')
        })
        .catch(err=>{
            console.log(err)
        })

    }
    return (
        <>
            <form className="addform" onSubmit={upload} autoComplete="off" >
                <h1>Add New Product</h1>
                <div>
                    <TextField label="Title" value={title} onChange={updateTitle}  />
                </div>
                <div>
                    <TextField label="Description"value={description} onChange={updatedesc}  />
                </div>
                <div>
                    <TextField label="image URL"value={imgurl} onChange={updateurl}  />
                </div>
                <div>
                    <TextField label="cost"value={cost} onChange={updatecost}  />
                </div>
                <div>
                    <TextField label="Rating"value={rate} onChange={updaterate}  />
                </div>
                <div>
                    <Button type="submit" variant="contained" color="success" disabled={!navigator.onLine }>Upload</Button>
                </div>
            </form>
        </>
    )
}
export default AddStock;
